select OUTS.ANIMAL_ID,OUTS.NAME from ANIMAL_OUTS as OUTS left join ANIMAL_INS as INS on OUTS.ANIMAL_ID = INS.ANIMAL_ID where INS.ANIMAL_ID is NULL order by OUTS.ANIMAL_ID;
select INS.ANIMAL_ID,INS.NAME from ANIMAL_INS as INS left join ANIMAL_OUTS as OUTS on INS.ANIMAL_ID = OUTS.ANIMAL_ID where INS.DATETIME > OUTS.DATETIME order by INS.DATETIME;
select INS.NAME,INS.DATETIME from ANIMAL_INS as INS left join ANIMAL_OUTS as OUTS on INS.ANIMAL_ID = OUTS.ANIMAL_ID where OUTS.ANIMAL_ID is NULL order by INS.DATETIME limit 0,3;
select INS.ANIMAL_ID,INS.ANIMAL_TYPE,INS.NAME from ANIMAL_INS as INS left join ANIMAL_OUTS as OUTS on INS.ANIMAL_ID = OUTS.ANIMAL_ID where INS.SEX_UPON_INTAKE like "Intact%" and NOT INS.SEX_UPON_INTAKE = OUTS.SEX_UPON_OUTCOME order by INS.ANIMAL_ID;